(()=>{"use strict";new class{constructor(){this.init(),this.events()}init(){document.querySelectorAll(".js--scroll-to").forEach((t=>{new class{constructor(t){this.DOM={trigger:t.trigger,anchorTo:t.anchorTo,offsetTopAttribute:t.offsetTopAttribute},this.checkUrl=t.checkUrl,this.offsetTop=t.offsetTop,this.offsetTopURL=t.offsetTopURL,this.init()}init(){if(this.checkUrl&&this.scrollFromURL(),this.DOM.trigger){const e=this.DOM.trigger.getAttribute(this.DOM.anchorTo),s=document.getElementById(e);if(this.DOM.offsetTopAttribute)var t=this.DOM.trigger.getAttribute(this.DOM.offsetTopAttribute);var o=t??this.offsetTop;this.DOM.trigger.addEventListener("click",(t=>{t.preventDefault(),this.scrollTo(s.offsetTop,o)}))}}events(){}scrollTo(t,o){window.scrollTo({top:t-o,left:0,behavior:"smooth"})}scrollFromURL(){const t=window.location.href,o=t.indexOf("?");if(o>0){const e=t.slice(o+1),s=document.getElementById(e);this.scrollTo(s.offsetTop,this.offsetTopURL)}}}({trigger:t,checkUrl:!0,anchorTo:"tf-data-target",offsetTopAttribute:"tf-data-distance",offsetTop:50,offsetTopURL:100})}))}events(){}}})();