(()=>{"use strict";new class{constructor(){this.init(),this.events()}init(){document.querySelectorAll(".js--scroll-to").forEach((t=>{new class{constructor(t){this.DOM={element:t.element},this.offsetTopAttr=this.DOM.element.getAttribute(t.offsetTopAttribute),this.targetId=this.DOM.element.getAttribute(t.anchorTo),this.checkUrl=t.checkUrl,this.offsetTop=t.offsetTop,this.offsetTopURL=t.offsetTopURL,this.init()}init(){if(this.checkUrl&&this.scrollFromURL(),this.DOM.element){const e=document.getElementById(this.targetId);var t=this.offsetTopAttr??this.offsetTop;this.DOM.element.addEventListener("click",(o=>{o.preventDefault(),this.scrollTo(e.offsetTop,t)}))}}events(){}scrollTo(t,e){window.scrollTo({top:t-e,left:0,behavior:"smooth"})}scrollFromURL(){const t=window.location.href,e=t.indexOf("?");if(e>0){const o=t.slice(e+1),s=document.getElementById(o);this.scrollTo(s.offsetTop,this.offsetTopURL)}}}({element:t,checkUrl:!0,anchorTo:"tf-data-target",offsetTopAttribute:"tf-data-distance",offsetTop:50,offsetTopURL:100})}))}events(){}}})();